def max_freq(d):
    max = 0
    f = 'a'
    for c in d:
        if d[c] > max:
            max = d[c]
            f = c
    return f

def most_frequent(str):
    freq = {}
    for c in str:
        if 97 <= ord(c) <= 122:
            freq[c] = freq.setdefault(c, 0) + 1
    for i in range(len(freq)):
        l = max_freq(freq)
        print(l)
        freq.pop(l)


def key(word):
    return list_to_str(sorted(list(word)))


def list_to_str(l):
    s = ''
    for i in l:
        s += i
    return s


def max_len(an):
    max = 1
    f = None
    for c in an:
        if len(an[c]) > max:
            max = len(an[c])
            f = c
    return f


def anagram_sets():
    file = open('words.txt')
    words = {}
    for w in file:
        word = w.strip()
        k = key(word)
        words.setdefault(k, []).append(word)
    for i in range(len(words)):
        x = max_len(words)
        print(words.pop(x))


def test(one, two):
    count = 0
    for i, j in zip(one, two):
        if i != j:
            count += 1
    return count == 2


def metathesis():
    file = open('words.txt')
    words = {}
    for w in file:
        word = w.strip()
        k = key(word)
        words.setdefault(k, []).append(word)
    for i in words:
        t = words[i]
        for j in range(len(t)):
            for k in range(j+1, len(t)):
                if test(t[j], t[k]):
                    print(t[j], t[k])

file = open('words.txt')
words = []
for i in file:
    words.append(i.strip())

def children(word):
    child = []
    for i in range(len(word)):
        child.append(word[:i] + word[i+1:])
    return child

def reducible(word):
    if word == '':
        return True
    elif list.contain(words, word):
        reduced = ''
        for i in children(word):
            if reducible(i):
                return True
        return False
    else:
        return False


def reducible_ext(word):
    if word == '':
        return [word]
    elif list.contain(words, word):
        reduced = ''
        for i in children(word):
            n = reducible_ext(i)
            if n != []:
                return [word] + n
        return []
    else:
        return []


def cartalkreduce():
    reducible_word = []
    for i in words:
        if reducible(i):
            reducible_word.append(i)
    max = 0
    longest_word = ''
    for i in reducible_word:
        if len(i) > max:
            max = len(i)
            longest_word = i
    print(longest_word, reducible_ext(longest_word))


